import 'package:financy/Tab_beneficiary/beneficiary_bank.dart';import 'package:financy/Tab_beneficiary/beneficiary_cash.dart';import 'package:flutter/material.dart';class AddBeneficiary extends StatefulWidget {  @override  _AddBeneficiaryState createState() => _AddBeneficiaryState();}enum PopOutMenu { HELP, ABOUT, CONTACT, SETTINGS }class _AddBeneficiaryState extends State<AddBeneficiary>    with SingleTickerProviderStateMixin {  TabController _tabController;  @override  void initState() {    super.initState();    _tabController = TabController(initialIndex: 0, length: 2, vsync: this);  }  @override  void dispose() {    _tabController.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text(          'Add New Beneficiary',          style: TextStyle(            fontFamily: 'Montserrat',            color: Colors.white,            fontSize: 18.0,          ),        ),        backgroundColor: Color(0xff2E2A97),        centerTitle: false,        bottom: TabBar(          indicatorColor: Colors.white,          tabs: [            Tab(              icon: Icon(Icons.home),              text: "Bank",            ),            Tab(              icon: Icon(Icons.attach_money),              text: "Cash",            ),          ],          controller: _tabController,        ),      ),      body: Center(        child: TabBarView(          children: [            BeneficiaryBank(),            BeneficiaryCash(),          ],          controller: _tabController,        ),      ),    );  }}