//import 'package:financy/screen/add_beneficiarie.dart';import 'package:financy/screen/send_money.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/painting.dart';import 'package:flutter/rendering.dart';import 'package:flutter/widgets.dart';import 'history_transfir.dart';class Dashboard extends StatefulWidget {  static const String id = '/Dashboard';  @override  _DashboardState createState() => _DashboardState();}class _DashboardState extends State<Dashboard> {  final List<String> _listButome = [    'Money Transfir',    'History Trasfir',    'Add New Beneficiary',    'Corrency converte',  ];  @override  Widget build(BuildContext context) {     final height = MediaQuery.of(context).size.height;    return Scaffold(      appBar: AppBar(        backgroundColor: Color(0xff2E2A97),        elevation: 0,        title: Text(          'Money Basket Exchange',          style: TextStyle(            fontFamily: 'Montserrat',            color: Colors.white,            fontWeight: FontWeight.bold,            fontSize: 18.0,          ),        ),        leading: Icon(null),        centerTitle: false,        actions: <Widget>[          IconButton(            icon: Icon(Icons.menu),            color: Colors.white,            onPressed: () {},          ),        ],      ),      backgroundColor: Color(0xff2E2A97), //Color(0xff21bfbd),      body: ListView(        children: <Widget>[          Padding(            padding: EdgeInsets.only(left: 26, right: 26, top: 26),            child: _hedar(),          ),          SizedBox(height: 30.0),          Container(            padding: EdgeInsets.only(left: 26, right: 26),            height: height - 1,//MediaQuery.of(context).size.height - 300.0, //300            decoration: BoxDecoration(              color: Colors.white,              borderRadius: BorderRadius.only(                topLeft: Radius.circular(75.0),        