import 'package:flutter/material.dart';import 'package:flutter/widgets.dart';class HistoryTransfir extends StatefulWidget {  @override  _HistoryTransfirState createState() => _HistoryTransfirState();}class _HistoryTransfirState extends State<HistoryTransfir> {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Color(0xff2E2A97), //Color(0xff21bfbd),      appBar: AppBar(        elevation: 0,        backgroundColor: Color(0xff2E2A97),        centerTitle: false,        title: Text(          'History Transfir',          style: TextStyle(            fontFamily: 'Montserrat',            color: Colors.white,            fontSize: 18.0,          ),        ),        actions: <Widget>[          IconButton(icon: Icon(Icons.search,size: 32 ,color: Colors.white,), onPressed: (){          })        ],      ),      body: ListView(        children: <Widget>[          SizedBox(height: 20.0),          Container(            height: MediaQuery.of(context).size.height, //-300            decoration: BoxDecoration(              color: Colors.white,              borderRadius: BorderRadius.only(                topLeft: Radius.circular(75.0),                //topRight: Radius.circular(75.0),              ),            ),            child: ListView(              primary: false,              padding: EdgeInsets.only(left: 25.0, right: 25.0),              children: <Widget>[                Padding(                  padding: EdgeInsets.only(top: 45.0),                  child: Container(                    height: MediaQuery.of(context).size.height - 50.0,                    child: ListView.builder(                      itemBuilder: (context, position) {                        return Card(                          elevation: 3,                          child: _drawSingleRow(                              'Ahmed saad Elsaid', '150', '20-3-2020'),                        );                      },                      itemCount: 20,                    ),                  ),                )              ],            ),          ),        ],      ),    );  }  // ignore: non_constant_identifier_names  Widget _drawSingleRow(BenName, Amount, TransDate) {    return Padding(      padding: const EdgeInsets.all(12.0),      child: Row(        children: <Widget>[          Expanded(            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: <Widget>[                Text(                  BenName,                  maxLines: 2,                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600,),                ),                SizedBox(                  height: 18,                ),                Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: <Widget>[                    Row(                      children: <Widget>[                        Icon(Icons.date_range, color: Colors.grey),                        Text(                          TransDate,                          style: TextStyle(                              fontSize: 16, fontWeight: FontWeight.w600),                        )                      ],                    ),                    Row(                      children: <Widget>[                        Icon(Icons.money_off, color: Colors.grey),                        Text(Amount,                            style: TextStyle(                                fontSize: 16, fontWeight: FontWeight.w600))                      ],                    ),                  ],                ),              ],            ),          ),        ],      ),    );  }}